<script src="https://cdnjs.cloudflare.com/ajax/libs/jspdf/1.3.4/jspdf.min.js"></script>
<script src="https://unpkg.com/jspdf-autotable@3.5.14/dist/jspdf.plugin.autotable.js"></script>
<script>
function download() {
	const doc = new jsPDF();
	doc.setFontSize(22);
	var imgData = "data:image/png;base64,";
	doc.addImage(imgData, 'PNG', 170, 10, 25, 25);
	doc.text("Smartlist", 15, 20);
	doc.setFontSize(13);
	doc.text("www.smartlist.ga", 15, 27);
	doc.setFontSize(10);
	doc.text("Inventory for: Dining Room", 15, 34);

	doc.autoTable({
		head: [['Item Name', 'Quantity']],
		body: [
			<?php 
                try {
                $dbh = new PDO("mysql:host=$servername;dbname=$dbname", $username, $password);
                $sql = "SELECT * FROM dining_room WHERE login_id=" . $_SESSION['id'];
                $users = $dbh->query($sql);
                foreach ($users as $row) {
                  echo "[".json_encode(decrypt($row['name'])).", ".json_encode(decrypt($row['qty']))."],";
                }
                $dbh = null;
              }
            catch(PDOexception $e) { echo "Error is: " . $e->etmessage(); }
            ?>
		],
		styles: { 
			theme: 'striped',
			cellPadding: 3,
		},
		startY: 50,
		headStyles: {
			fillColor: [200, 200, 200],
			textColor: [0, 0, 0]
		}
	})
	doc.save("inventory.pdf");
}
download()
</script>
<script>M.toast({html: "Downloaded file as inventory.pdf"})</script>